import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [isAddNewExpense, setisAddNewExpense] = useState(true);

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    /**
     * You can do setEnteredAmount((prevState) => return {...}) since prevState exists.
     */
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault(); // this just prevents the default behaviour of Javascript.
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    props.onSaveExpenseData(expenseData); // since this is now part of the paroperty that we are passing.
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };

  // Two way binding we can change the input programmatically. This is done through the value = {the state name}

  const addNewExpenseHandler = () => {
      setisAddNewExpense(false);
  }

  const cancelAddExpenseHandler = () => {
    setisAddNewExpense(true);
}


  if (isAddNewExpense) {
      return <div className="new-expense__controls">
          <button className = "new-expense__control" onClick={addNewExpenseHandler}>Add New Expense</button>
      </div>
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>

          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            value={enteredAmount}
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            value={enteredDate}
            min="2019-01-01"
            step="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
        <div className="new-expense__actions">
          <button onClick={cancelAddExpenseHandler}> Cancel</button>
          <button type="submit">Add Expense!</button>
        </div>
      </div>
    </form>
  );
};

export default ExpenseForm;
